const runSlider=function(){const e=document.querySelector(".progress-slider__input"),t=document.querySelector(".example__img-box--before"),r=document.querySelector(".example__img-box--after"),n=document.querySelector(".progress-slider__button--start"),i=document.querySelector(".progress-slider__button--end");e.addEventListener("input",(function(){t.style.width=100-e.value+"%",r.style.width=e.value+"%"})),n.addEventListener("click",(function(){t.style.width="100%",r.style.width=0,e.value=0})),i.addEventListener("click",(function(){r.style.width="100%",t.style.width=0,e.value=100}))};runSlider();